mapper.update:
UPDATE movie_board SET
hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT group_id,group_step,group_tab
FROM movie_reply
WHERE no=#{no}

mapper.update:
UPDATE movie_reply SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE movie_reply SET
depth=depth+1
WHERE no=#{no}
